import { useState, useEffect } from 'react'
import './App.css'
import Loader from './components/loader';   // ðŸ‘ˆ ADDED
import { useNavigate } from 'react-router-dom';

const items = [
  { id: 1, name: "Flange", hasSizes:true, image: "data:image/jpeg;base64," },
  { id: 2, name: "Valve", hasSizes:false, image: "data:image/jpeg;base64," },
  { id: 3, name: "CI-CV", hasSizes:true, image: "data:image/jpeg;base64," },
  { id: 4, name: "Pipe", hasSizes:true, image: "data:image/jpeg;base64," },
  { id: 5, name: "Bolt", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 6, name: "Img 6", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 7, name: "Bolt", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 8, name: "Img 8", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 9, name: "Img 9", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 10, name: "Bolt", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 11, name: "Img 11", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 12, name: "Img 12", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 13, name: "Img 13", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 14, name: "Img 14", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 15, name: "Img 15", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 16, name: "Img 16", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 17, name: "Img 17", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 18, name: "Bolt", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 19, name: "Img 19", hasSizes:false, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
  { id: 20, name: "Img 20", hasSizes:true, image: "https://www.aquaristikshop.com/pictures_xxl/190050.jpg" },
];

function App({ searchTerm }) {

  // ðŸ‘‡ LOADING STATE ADDED
  const [loading, setLoading] = useState(true);
  const [highlightedId, setHighlightedId] = useState(null);

  useEffect(() => {
    // simulate network / image load delay
    const timer = setTimeout(() => {
      setLoading(false);
    }, 300);          // 0.3 sec â€“ good UX

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
  if (!searchTerm) {
    setHighlightedId(null);
    return;
  }

  const foundItem = items.find(item => 
    item.name.toLowerCase()===searchTerm.toLowerCase().trim()
  );
  
  if (foundItem) {
      setHighlightedId(foundItem.id);

      // 2. Scroll to the item
      // We use a tiny timeout to ensure the DOM is ready
      setTimeout(() => {
        const element = document.getElementById(`product-${foundItem.id}`);
        if (element) {
          element.scrollIntoView({
            behavior: "smooth",
            block: "center", // Moves the found item to the middle of the screen
          });
        }
      }, 100);
    } else {
      setHighlightedId(null);
    }
}, [searchTerm]);

  const navigate = useNavigate();

  const handleItemClick = (item) => {
    if (item.hasSizes) {
      navigate(`/product/${item.id}`); // Redirects to size details page
    }
  };

  if (loading) return <Loader />;

   return (
    <>
      <div className="app-content">
        <h2>Welcome to Maa Bhavani Enterprises!</h2>
        <div className="items-container" style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "20px", padding: "1rem" }}>
          {items.map(item => (
            <div
              key={item.id}
              id={`product-${item.id}`}
              onClick={() => handleItemClick(item)}
              className={`item-card ${highlightedId === item.id ? 'highlight' : ''}`}
              style={{
                cursor: item.hasSizes ? 'pointer' : 'default',
                border: highlightedId === item.id ? '3px solid orange' : '1px solid #ccc',
                borderRadius: '8px',
                padding: '10px',
                width: '200px',
                textAlign: 'center',
                transition: 'border 0.3s ease',
              }}
            >
              <img src={item.image} alt={item.name} style={{ width: "100%", height: "150px", objectFit: "contain" }} />
              <h3>{item.name}</h3>
            </div>
          ))}
        </div >
      </div>
    </>
  );
}

export default App;
